package me.rail.ideasworldtest.network.repos.itemimport me.rail.ideasworldtest.models.item.Itemimport me.rail.ideasworldtest.network.ApiResultimport me.rail.ideasworldtest.network.HttpClientimport javax.inject.Injectclass HttpItemRepo @Inject constructor(    private val client: HttpClient): ItemRepo {    override suspend fun getItem(id: String): ApiResult<Item> {        val itemResponse = client.getItem(id = id)        if (itemResponse.isSuccessful) {            return ApiResult.Success(itemResponse.body())        }        val errorMsg = itemResponse.errorBody()?.string()        itemResponse.errorBody()?.close()        return ApiResult.Error(errorMsg)    }}