package me.rail.ideasworldtest.network.repos.listimport me.rail.ideasworldtest.models.list.Photoimport me.rail.ideasworldtest.network.HttpClientimport javax.inject.Injectclass HttpPhotoRepo @Inject constructor(    private val client: HttpClient) : PhotoRepo {    override suspend fun getPhotos(): MutableList<Photo> {        val photos = client.getPhotos()        photos.addAll(client.getPhotos(page = 2, per_page = 20))        return photos    }}